(108, 0, 'INICIO DE LANÇAMENTOS / NOVA EMPRESA', '2012-07-31 16:43:00', '2012-07-31 16:43:00', '2012-07-31 16:43:00', '2012-07-31 16:45:57', 562, 6, 562, 1, 'Software', 14, 'Solicitação aberta diretamente no suporte CHB (2012/038900).\r\n\r\n31/07/2012 16:18 CHB CHEREGHINI:\r\nPARA ALTERAÇÃO DE INICIO DE LANÇAMENTOS CONTÁBEIS COMO PROCEDER =&gt; CONTÁBIL / EMPRESA / DADOS DA EMPRESA = PREENCHER A DATA DE INICIO DOS LANÇAMENTOS CONTÁBEIS.', 3, 3, 3, 0, 1, 0, '', 'none', 0, 0, NULL, NULL, 0, 0, 0, 0, 140, 0, 0, 0),
(109, 0, 'CONTABILIZAÇÃO DE ADIANTAMENTOS', '2012-08-03 08:45:00', '2012-10-03 11:27:52', '2012-10-03 11:27:52', '2012-10-03 11:27:52', 562, 6, 472, 8, 'Software', 11, 'Solicitação aberta na CHB 2012/039611.\r\n\r\nNa integração de adiantamento de parceria (processo P01) enviado pelo pims, o chb está contabilizando no contas a pagar do passivo como débito, sendo que por se tratar de adiantamento deveria ser contabilizado no ativo em adiantamento de fornecedores de cana (8989).\r\n\r\nNo caso de adiantamentos (processo C01) foi contabilizado corretamente.', 3, 3, 3, 0, 1, 0, '', 'none', 0, 0, NULL, NULL, 0, 0, 5280172, 5280172, 100, 0, 0, 0),
(110, 0, 'INTEGRAÇÃO PIMS X CHB ', '2012-08-03 10:53:00', '2012-08-03 10:53:00', '2012-08-03 10:53:00', '2012-08-03 10:54:30', 562, 6, 562, 1, 'Software', 16, 'Atendimento CHB 2012/039561.\r\n\r\n02/08/2012 18:10 CHB FERREIRA:\r\nCLIENTE: TEM LANÇAMENTO DO PIMS QUE FOI CANCELADO ANTES DE FAZER A INTEGRAÇÃO COM O CHB, SÓ QUE MESMO CANCELADO A ROTINA DO CHB FEZ A INTEGRAÇÃO E MOVIMENTOU O ESTOQUE........ FERREIRA: VERIFICADO QUE HAVIA DOIS LANÇAMENTO NA CONDIÇÃO DE CANCELADO NO PIMS PORÉM COM MOVIMENTAÇÃO NO CHB, PRODUTO 2194 - DATA DA MOVIMENTAÇÃO 25/07/2012 - QTDE 107, PRODUTO 652532 - DATA DA MOVIMENTAÇÃO 24/07/2012 - QTDE 2, ESSES DOIS LANÇAMENTOS FORA EXCLUÍDOS VIA BD, POIS PELO SISTEMA MESMO TIRANDO A ORIGEM NÃO ESTAVA EXCLUINDO FICAVA TRAVADO, ORIENTADO O ANDERSON A MONITORAR AS INTEGRAÇÕES PARA VERIFICAR SE VAI OCORRER MAIS ALGUM ERRO DESSE TIPO, QUALQUER PROBLEMA ENTRAR EM CONTATO COM O SUPORTE', 3, 3, 3, 0, 1, 0, '', 'none', 0, 0, NULL, NULL, 0, 0, 0, 0, 90, 0, 0, 0),
(111, 0, 'PROBLEMAS INTEGRAÇÃO PIMS\\CHB ', '2012-08-03 10:56:00', '2012-08-03 10:56:00', '2012-08-03 10:56:00', '2012-08-03 11:12:38', 562, 6, 562, 1, 'Software', 11, 'Solicitação CHB 2012/039333.\r\n\r\n02/08/2012 16:16 CHB DOMINGOS:\r\nTIVEMOS PROBLEMAS AO FAZER INTEGRAÇÃO NO MOVIMENTO DA SEGUNDA QUINZENA DE JULHO. O SISTEMA GEROU ITENS DE PEDIDO DOS PRESTADORES DE SERVIÇO EM PEDIDOS QUE JÁ ESTAVAM CRIADOS NA PRIMEIRA QUINZENA E INCLUSIVE ENCERRADO COM NOTA FISCAL. ANALISADO IDENTIFICAMOS QUE HOUVE O PROBLEMA POIS AO INTEGRAR OS PEDIDOS O SISTEMA FAZ UMA REGRA DE AGRUPAMENTO DE ITENS CONSIDERANDO A (DATA DE SERVIÇO, FORNECEDOR, IBGE). VERIFICAMOS QUE TANTO A 1º QUINZENA E 2º QUINZENA DE JULHO ESTAVAM COM A DATA DE SERVIÇO "31/07/2012", ASSIM O SISTEMA AGRUPOU ITENS EM PEDIDO JÁ ENCERRADOS. PARA ESTA SITUAÇÃO SERÁ ABERTO CT PARA CRIAR TRAVA E ALERTA NA ROTINA IMPEDINDO A INSERÇÃO DE ITENS EM PEDIDO "AUTORIZADOS" OU "ENCERRADOS". INFORMEI QUE CONFORME DESENVOLVIMENTO DA CT 2012/35805-1 NO CAMPO DATA SERVIÇO SERIA INFORMADO PELO PIMS O ULTIMO DIA DA QUINZENA, CASO A INFORMAÇÃO NÃO VENHA DESTA FORMA TEREMOS PROBLEMAS COM O CHB NA INTEGRAÇÃO FINANCEIRO\\CONTABIL E PEDIDOS DE COMPRA. INFORMADO QUE CASO MUDE A REGRA NÃO CONSIDERANDO A "DATA_SERVICO" TEREMOS QUE ALTERAR TODA ROTINA DE INTEGRAÇÃO QUE JA CONSIDERA ESTE CAMPO E CRIAR REGRA PARA TRATAR A DATA ENVIADA PELO PIMS. PARA CORRIGIR O PROBLEMA DA INTEGRAÇÃO FOI FEITO O DESENVOLVIMENTO DE UM PINDURA PARA ESTORNAR OS ITENS\\PEDIDOS GERADOS ERRADO. DEPOIS DE EXECUTADO O PINDURA E CORRIGIDO A BASE FOI FEITO A INTEGRAÇÃO NOVAMENTE E FICOU CORRETO. SERÁ FEITO AS CONFERÊNCIAS E SE NECESSARIO ENTRARAM EM CONTATO COM O SUPORTE. CASO MUDE A REGRA PARA INTEGRAÇÃO DAS DATAS O CLIENTE ENTRARÁ EM CONTATO.', 3, 3, 3, 0, 1, 0, '', 'none', 0, 0, NULL, NULL, 0, 0, 0, 0, 998, 0, 0, 0);
